mapper.select:
		select `id`,`name`,`type`,`for_names`,`for_ids`,`for_type`,`for_details`,`add_time`,`update_time`,`delete_flag` from t_msg

		  		   and id = #{id} 
		  		   and name = #{name} 
		  		   and type = #{type} 
		  		   and for_names = #{forNames} 
		  		   and for_ids = #{forIds} 
		  		   and for_type = #{forType} 
		  		   and for_details = #{forDetails} 
		  		   and add_time = #{addTime} 
		  		   and update_time = #{updateTime} 
		  		   and delete_flag = #{deleteFlag} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from t_msg

  		   and id = #{id} 
  		   and name = #{name} 
  		   and type = #{type} 
  		   and for_names = #{forNames} 
