mapper.select:
		select `id`,`user_id`,`add_id`,`type_id`,`category_id`,`repair_time`,`details`,`add_time`,`money`,`statue` from t_repair

		  		   and id = #{id} 
		  		   and user_id = #{userId} 
		  		   and add_id = #{addId} 
		  		   and type_id = #{typeId} 
		  		   and category_id = #{categoryId} 
		  		   and repair_time = #{repairTime} 
		  		   and details = #{details} 
		  		   and add_time = #{addTime} 
		  		   and money = #{money} 
		  		   and statue = #{statue} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from t_repair

  		   and id = #{id} 
  		   and user_id = #{userId} 
  		   and add_id = #{addId} 
  		   and type_id = #{typeId} 
  		   and category_id = #{categoryId} 
  		   and repair_time = #{repairTime} 
  		   and details = #{details} 
  		   and add_time = #{addTime} 
  		   and money = #{money} 
  		   and statue = #{statue}
